function session = initSession(paradigm)% Initialize entire running sessionsession = [];subjnum=getInput('Enter the subject number: ');if SubjExists(subjnum)   % Check if the subject already exists in data folder	error('Script aborted.');end% counterbalance response keys based on subject numberRespCounterBalance = mod(subjnum,4);switch RespCounterBalance    case 0        CounterBalanceDisc = 'n';        CounterBalanceIm = 'n';    case 1        CounterBalanceDisc = 'n';        CounterBalanceIm = 'y';    case 2        CounterBalanceDisc = 'y';        CounterBalanceIm = 'n';    case 3        CounterBalanceDisc = 'y';        CounterBalanceIm = 'y';endsubjage=getInput('Enter the subject''s age: ');subjgend=getInput('Enter the subject''s gender: (f/m/n) ','s',['m','f','n']);subjhand=getInput('Enter the subject''s handedness: (r/l) ','s',['r','l']);% create session objectsession = struct('paradigm',paradigm,'datetime',datestr(now),...					'version',version,'subjnum',subjnum,'CounterBalanceDisc',...                    CounterBalanceDisc,'CounterBalanceIm',CounterBalanceIm,...                    'params',[],'subject',struct('age',subjage,'gender',subjgend,...                    'hand',subjhand),'notes','');                tmp = inputdlg('Notes','Notes',7);session.notes = tmp{1};session.params = initParams(session); % initialize session parameterssession.triggers = initBIO(session);  % get trigger valuesend