function session = initSession(paradigm)session = [];subjnum=getInput('Enter the subject number: ');if SubjExists(subjnum)	error('Script aborted.');endRespCounterBalance=getInput('Change response keys? ','s',['y','n']);subjage=getInput('Enter the subject''s age: ');subjgend=getInput('Enter the subject''s gender: ','s',['m','f']);subjhand=getInput('Enter the subject''s handedness: ','s',['r','l']);% create session objectsession = struct('paradigm',paradigm,'datetime',datestr(now),...					'version',version,'subjnum',subjnum,'RespCounterBalance',RespCounterBalance,'params',[],...					'subject',struct('age',subjage,'gender',subjgend,'hand',subjhand),...					'notes','');                tmp = inputdlg('Notes','Notes',7);session.notes = tmp{1};session.params = initParams(session);session.stimuli = initStimuli(session.params);end